import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { catchError, timeout } from 'rxjs';

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html'
})
export class FetchDataComponent {
  public newestStories: Item[] = [];
  public page: number = 0;
  public search: string = '';

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    //http.get<WeatherForecast[]>(baseUrl + 'weatherforecast').subscribe(result => {
    //  this.forecasts = result;
    //}, error => console.error(error));
    http.get<Item[]>(baseUrl + 'hackernews').pipe(
      timeout(30000),
      catchError((error)=>{
        if (error.name === 'TimeoutError') {
          // Handle timeout error (e.g., show a message to the user)
          return throwError('Request timed out. Please try again later.');
        }
        // Handle other errors here
        return throwError('An error occurred while fetching data.');
      })
    ).subscribe(result => {
      console.log(result);
      this.newestStories = result;
    }, error => console.error(error));
  }

  nextPage() {
    this.page += 5;
  }

  prevPage() {
    if ( this.page > 0 )
      this.page -= 5;
  }

  onSearchTitle( search: string ) {
    this.page = 0;
    this.search = search;
  }
}




export interface Item {
  title: string;
  url: string;
}
<h1 id="tableLabel">Newest Stories</h1>

<p>This component demonstrates fetching data from the HackerAPI.</p>

<p *ngIf="newestStories === []"><em>Loading...</em></p>

<input type="text"
        class="form-control mb-2"
        placeholder="Search in Title..."
        #txtSearch
        (keyup)="onSearchTitle( txtSearch.value )" />

<!-- <button class="button">
  Previuos
</button>
<button>
  Next
</button> -->

<button class="btn btn-primary"
        (click)="prevPage()">
        Previuos
</button>
&nbsp;
<button class="btn btn-primary"
        (click)="nextPage()"
        [disabled]="(newestStories | filter:page:search).length === 0">
        Next
</button>


<table class='table table-striped' aria-labelledby="tableLabel">
  <thead>
    <tr>
      <th>Title</th>
      <th>Link</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let item of newestStories | filter:page:search">
      <td>{{ item.title }}</td>
      <td><a href="{{ item.url }}" target="_blank">Go to story</a></td>
    </tr>
  </tbody>
</table>
